// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.8;

import "../ERC20GuildUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";

/*
  @title DXDGuild
  @author github:AugustoL
  @dev An ERC20GuildUpgradeable for the DXD token designed to execute votes on Genesis Protocol Voting Machine.
*/
contract DXDGuild is ERC20GuildUpgradeable, OwnableUpgradeable {
    using SafeMathUpgradeable for uint256;

    // @dev Initilizer
    // @param _token The ERC20 token that will be used as source of voting power
    // @param _proposalTime The amount of time in seconds that a proposal will be active for voting
    // @param _timeForExecution The amount of time in seconds that a proposal action will have to execute successfully
    // @param _votingPowerPercentageForProposalExecution The percentage of voting power in base 10000 needed to execute a proposal
    // action
    // @param _votingPowerPercentageForProposalCreation The percentage of voting power in base 10000 needed to create a proposal
    // @param _voteGas The amount of gas in wei unit used for vote refunds
    // @param _maxGasPrice The maximum gas price used for vote refunds
    // @param _maxActiveProposals The maximum amount of proposals to be active at the same time
    // @param _lockTime The minimum amount of seconds that the tokens would be locked
    // @param _permissionRegistry The address of the permission registry contract to be used
    // @param _votingMachine The voting machine where the guild will vote
    function initialize(
        address _token,
        uint256 _proposalTime,
        uint256 _timeForExecution,
        uint256 _votingPowerPercentageForProposalExecution,
        uint256 _votingPowerPercentageForProposalCreation,
        uint256 _voteGas,
        uint256 _maxGasPrice,
        uint256 _maxActiveProposals,
        uint256 _lockTime,
        address _permissionRegistry,
        address _votingMachine
    ) public initializer {
        __Ownable_init();
        super.initialize(
            _token,
            _proposalTime,
            _timeForExecution,
            _votingPowerPercentageForProposalExecution,
            _votingPowerPercentageForProposalCreation,
            "DXDGuild",
            _voteGas,
            _maxGasPrice,
            _maxActiveProposals,
            _lockTime,
            _permissionRegistry
        );
        permissionRegistry.setETHPermission(
            address(this),
            _votingMachine,
            bytes4(keccak256("vote(bytes32,uint256,uint256,address)")),
            0,
            true
        );
    }
}

contracts/erc20guild/ERC20Guild.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.8;

import "./BaseERC20Guild.sol";

/*
  @title ERC20Guild
  @author github:AugustoL
  @dev Non upgradeable ERC20Guild
*/
contract ERC20Guild is BaseERC20Guild {
    // @dev Constructor
    // @param _token The ERC20 token that will be used as source of voting power
    // @param _proposalTime The amount of time in seconds that a proposal will be active for voting
    // @param _votingPowerPercentageForProposalExecution The percentage of voting power in base 10000 needed to execute a proposal
    // action
    // @param _votingPowerPercentageForProposalCreation The percentage of voting power in base 10000 needed to create a proposal
    // @param _name The name of the ERC20Guild
    // @param _maxActiveProposals The maximum amount of proposals to be active at the same time
    // @param _lockTime The minimum amount of seconds that the tokens would be locked
    // @param _permissionRegistry The address of the permission registry contract to be used
    constructor(
        address _token,
        uint256 _proposalTime,
        uint256 _votingPowerPercentageForProposalExecution,
        uint256 _votingPowerPercentageForProposalCreation,
        string memory _name,
        uint256 _lockTime,
        address _permissionRegistry
    ) {
        require(address(_token) != address(0), "ERC20Guild: token cant be zero address");
        require(_proposalTime > 0, "ERC20Guild: proposal time has to be more tha 0");
        require(_lockTime >= _proposalTime, "ERC20Guild: lockTime has to be higher or equal to proposalTime");
        require(
            _votingPowerPercentageForProposalExecution > 0,
            "ERC20Guild: voting power for execution has to be more than 0"
        );
        name = _name;
        token = IERC20Upgradeable(_token);
        tokenVault = new TokenVault(address(token), address(this));
        proposalTime = _proposalTime;
        votingPowerPercentageForProposalExecution = _votingPowerPercentageForProposalExecution;
        votingPowerPercentageForProposalCreation = _votingPowerPercentageForProposalCreation;
        lockTime = _lockTime;
        permissionRegistry = PermissionRegistry(_permissionRegistry);

        // This variables are set initially to default values cause the constructor throws stack too deep error
        // They can be changed later by calling the setConfig function
        timeForExecution = 30 days;
        voteGas = 0;
        maxGasPrice = 0;
        maxActiveProposals = 5;
    }
}

contracts/erc20guild/implementations/Mig.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.8;

import "../ERC20Guild.sol";

/*
  @title MigratableERC20Guild
  @author github:AugustoL
  @dev An ERC20Guild that can migrate from one ERC20 voting token to another by changing token vault
*/
contract MigratableERC20Guild is ERC20Guild {
    using SafeMathUpgradeable for uint256;

    // The tokens locked indexed by token holder address.
    mapping(address => mapping(address => TokenLock)) public tokensLockedByVault;

    // The total amount of tokens locked
    mapping(address => uint256) public totalLockedByVault;

    uint256 public lastMigrationTimestamp;

    // @dev Constructor
    // @param _token The ERC20 token that will be used as source of voting power
    // @param _proposalTime The amount of time in seconds that a proposal will be active for voting
    // @param _votingPowerPercentageForProposalExecution The percentage of voting power in base 10000 needed to execute a proposal
    // action
    // @param _votingPowerPercentageForProposalCreation The percentage of voting power in base 10000 needed to create a proposal
    // @param _name The name of the ERC20Guild
    // @param _maxActiveProposals The maximum amount of proposals to be active at the same time
    // @param _lockTime The minimum amount of seconds that the tokens would be locked
    // @param _permissionRegistry The address of the permission registry contract to be used
    constructor(
        address _token,
        uint256 _proposalTime,
        uint256 _votingPowerPercentageForProposalExecution,
        uint256 _votingPowerPercentageForProposalCreation,
        string memory _name,
        uint256 _lockTime,
        address _permissionRegistry
    )
        ERC20Guild(
            _token,
            _proposalTime,
            _votingPowerPercentageForProposalExecution,
            _votingPowerPercentageForProposalCreation,
            _name,
            _lockTime,
            _permissionRegistry
        )
    {}

    // @dev Change the token vault used, this will change the voting token too.
    // The token vault admin has to be the guild.
    // @param newTokenVault The address of the new token vault
    function changeTokenVault(address newTokenVault) external virtual {
        require(msg.sender == address(this), "MigratableERC2Guild: The vault can be changed only by the guild");
        tokenVault = TokenVault(newTokenVault);
        require(tokenVault.getAdmin() == address(this), "MigratableERC2Guild: The vault admin has to be the guild");
        token = IERC20Upgradeable(tokenVault.getToken());
        require(
            newTokenVault.codehash == keccak256(abi.encodePacked(type(TokenVault).runtimeCode)),
            "MigratableERC2Guild: Wrong code of newTokenVault"
        );
        lastMigrationTimestamp = block.timestamp;
    }

    // @dev Lock tokens in the guild to be used as voting power in the official vault
    // @param tokenAmount The amount of tokens to be locked
    function lockTokens(uint256 tokenAmount) external virtual override {
        tokenVault.deposit(msg.sender, tokenAmount);
        if (tokensLockedByVault[address(tokenVault)][msg.sender].amount == 0) totalMembers = totalMembers.add(1);
        tokensLockedByVault[address(tokenVault)][msg.sender].amount = tokensLockedByVault[address(tokenVault)][
            msg.sender
        ].amount.add(tokenAmount);
        tokensLockedByVault[address(tokenVault)][msg.sender].timestamp = block.timestamp.add(lockTime);
        totalLockedByVault[address(tokenVault)] = totalLockedByVault[address(tokenVault)].add(tokenAmount);
        emit TokensLocked(msg.sender, tokenAmount);
    }

    // @dev Withdraw tokens locked in the guild form the official vault, this will decrease the voting power
    // @param tokenAmount The amount of tokens to be withdrawn
    function withdrawTokens(uint256 tokenAmount) external virtual override {
        require(
            votingPowerOf(msg.sender) >= tokenAmount,
            "MigratableERC2Guild: Unable to withdraw more tokens than locked"
        );
        require(
            tokensLockedByVault[address(tokenVault)][msg.sender].timestamp < block.timestamp,
            "MigratableERC2Guild: Tokens still locked"
        );
        tokensLockedByVault[address(tokenVault)][msg.sender].amount = tokensLockedByVault[address(tokenVault)][
            msg.sender
        ].amount.sub(tokenAmount);
        totalLockedByVault[address(tokenVault)] = totalLockedByVault[address(tokenVault)].sub(tokenAmount);
        tokenVault.withdraw(msg.sender, tokenAmount);
        if (tokensLockedByVault[address(tokenVault)][msg.sender].amount == 0) totalMembers = totalMembers.sub(1);
        emit TokensWithdrawn(msg.sender, tokenAmount);
    }

    // @dev Lock tokens in the guild to be used as voting power in an external vault
    // @param tokenAmount The amount of tokens to be locked
    // @param _tokenVault The token vault to be used
    function lockExternalTokens(uint256 tokenAmount, address _tokenVault) external virtual {
        require(
            address(tokenVault) != _tokenVault,
            "MigratableERC2Guild: Use default lockTokens(uint256) function to lock in official vault"
        );
        TokenVault(_tokenVault).deposit(msg.sender, tokenAmount);
        tokensLockedByVault[_tokenVault][msg.sender].amount = tokensLockedByVault[_tokenVault][msg.sender].amount.add(
            tokenAmount
        );
        tokensLockedByVault[_tokenVault][msg.sender].timestamp = block.timestamp.add(lockTime);
        totalLockedByVault[_tokenVault] = totalLockedByVault[_tokenVault].add(tokenAmount);
        emit TokensLocked(msg.sender, tokenAmount);
    }

    // @dev Withdraw tokens locked in the guild from an external vault
    // @param tokenAmount The amount of tokens to be withdrawn
    // @param _tokenVault The token vault to be used
    function withdrawExternalTokens(uint256 tokenAmount, address _tokenVault) external virtual {
        require(
            address(tokenVault) != _tokenVault,
            "MigratableERC2Guild: Use default withdrawTokens(uint256) function to withdraw from official vault"
        );
        require(
            tokensLockedByVault[_tokenVault][msg.sender].timestamp < block.timestamp,
            "MigratableERC2Guild: Tokens still locked"
        );
        tokensLockedByVault[_tokenVault][msg.sender].amount = tokensLockedByVault[_tokenVault][msg.sender].amount.sub(
            tokenAmount
        );
        totalLockedByVault[_tokenVault] = totalLockedByVault[_tokenVault].sub(tokenAmount);
        TokenVault(_tokenVault).withdraw(msg.sender, tokenAmount);
        emit TokensWithdrawn(msg.sender, tokenAmount);
    }

    // @dev Executes a proposal that is not votable anymore and can be finished
    // If this function is called by the guild guardian the proposal can end sooner after proposal endTime
    // If this function is not called by the guild guardian the proposal can end sooner after proposal endTime plus
    // the extraTimeForGuardian
    // @param proposalId The id of the proposal to be executed
    function endProposal(bytes32 proposalId) public virtual override {
        if (proposals[proposalId].startTime < lastMigrationTimestamp) {
            proposals[proposalId].state = ProposalState.Failed;
            emit ProposalStateChanged(proposalId, uint256(ProposalState.Failed));
        } else {
            super.endProposal(proposalId);
        }
    }

    // @dev Get the voting power of an account
    // @param account The address of the account
    function votingPowerOf(address account) public view virtual override returns (uint256) {
        return tokensLockedByVault[address(tokenVault)][account].amount;
    }

    // @dev Get the locked timestamp of a voter tokens
    function getVoterLockTimestamp(address voter) public view virtual override returns (uint256) {
        return tokensLockedByVault[address(tokenVault)][voter].timestamp;
    }

    // @dev Get the totalLocked
    function getTotalLocked() public view virtual override returns (uint256) {
        return totalLockedByVault[address(tokenVault)];
    }
}

contracts/erc20guild/IERC20Guild.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.8;

interface IERC20Guild {
    event ProposalStateChanged(bytes32 indexed proposalId, uint256 newState);
    event VoteAdded(bytes32 indexed proposalId, address voter, uint256 votingPower);
    event SetAllowance(address indexed to, bytes4 functionSignature, bool allowance);

    enum ProposalState {
        None,
        Active,
        Rejected,
        Executed,
        Failed
    }

    struct Vote {
        uint256 option;
        uint256 votingPower;
    }

    struct Proposal {
        address creator;
        uint256 startTime;
        uint256 endTime;
        address[] to;
        bytes[] data;
        uint256[] value;
        string title;
        string contentHash;
        ProposalState state;
        uint256[] totalVotes;
    }

    fallback() external payable;

    receive() external payable;

    function initialize(
        address _token,
        uint256 _proposalTime,
        uint256 _timeForExecution,
        uint256 _votingPowerPercentageForProposalExecution,
        uint256 _votingPowerPercentageForProposalCreation,
        string memory _name,
        uint256 _voteGas,
        uint256 _maxGasPrice,
        uint256 _maxActiveProposals,
        uint256 _lockTime,
        address _permissionRegistry
    ) external;

    function setConfig(
        uint256 _proposalTime,
        uint256 _timeForExecution,
        uint256 _votingPowerPercentageForProposalExecution,
        uint256 _votingPowerPercentageForProposalCreation,
        uint256 _voteGas,
        uint256 _maxGasPrice,
        uint256 _maxActiveProposals,
        uint256 _lockTime,
        address _permissionRegistry
    ) external;

    function setPermission(
        address[] memory asset,
        address[] memory to,
        bytes4[] memory functionSignature,
        uint256[] memory valueAllowed,
        bool[] memory allowance
    ) external;

    function setPermissionDelay(uint256 permissionDelay) external;

    function createProposal(
        address[] memory to,
        bytes[] memory data,
        uint256[] memory value,
        uint256 totalOptions,
        string memory title,
        string memory contentHash
    ) external returns (bytes32);

    function endProposal(bytes32 proposalId) external;

    function setVote(
        bytes32 proposalId,
        uint256 option,
        uint256 votingPower
    ) external;

    function setVotes(
        bytes32[] memory proposalIds,
        uint256[] memory options,
        uint256[] memory votingPowers
    ) external;

    function setSignedVote(
        bytes32 proposalId,
        uint256 option,
        uint256 votingPower,
        address voter,
        bytes memory signature
    ) external;

    function setSignedVotes(
        bytes32[] memory proposalIds,
        uint256[] memory options,
        uint256[] memory votingPowers,
        address[] memory voters,
        bytes[] memory signatures
    ) external;

    function lockTokens(uint256 tokenAmount) external;

    function withdrawTokens(uint256 tokenAmount) external;

    function votingPowerOf(address account) external view returns (uint256);

    function votingPowerOfMultiple(address[] memory accounts) external view returns (uint256[] memory);

    function getToken() external view returns (address);

    function getPermissionRegistry() external view returns (address);

    function getName() external view returns (string memory);

    function getProposalTime() external view returns (uint256);

    function getTimeForExecution() external view returns (uint256);

    function getVoteGas() external view returns (uint256);

    function getMaxGasPrice() external view returns (uint256);

    function getMaxActiveProposals() external view returns (uint256);

    function getTotalProposals() external view returns (uint256);

    function getTotalMembers() external view returns (uint256);

    function getActiveProposalsNow() external view returns (uint256);

    function getMinimumMembersForProposalCreation() external view returns (uint256);

    function getMinimumTokensLockedForProposalCreation() external view returns (uint256);

    function getSignedVote(bytes32 signedVoteHash) external view returns (bool);

    function getProposalsIds() external view returns (bytes32[] memory);

    function getTokenVault() external view returns (address);

    function getLockTime() external view returns (uint256);

    function getTotalLocked() external view returns (uint256);

    function getVoterLockTimestamp(address voter) external view returns (uint256);

    function getProposal(bytes32 proposalId) external view returns (Proposal memory);

    function getProposalVotesOfVoter(bytes32 proposalId, address voter)
        external
        view
        returns (uint256 option, uint256 votingPower);

    function getVotingPowerForProposalCreation() external view returns (uint256);

    function getVotingPowerForProposalExecution() external view returns (uint256);

    function getFuncSignature(bytes memory data) external view returns (bytes4);

    function getProposalsIdsLength() external view returns (uint256);

    function getEIP1271SignedHash(bytes32 _hash) external view returns (bool);

    function isValidSignature(bytes32 hash, bytes memory signature) external view returns (bytes4 magicValue);

    function hashVote(
        address voter,
        bytes32 proposalId,
        uint256 option,
        uint256 votingPower
    ) external pure returns (bytes32);
}

contracts/utils/ETHRelayer.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.8;

/**
 * @title ETHRelayer
 * @dev Ether relayer used to relay all ether received in this contract to the receiver address.
 * Receives ETH via legacy .transfer function using defualt 23000 gas limit and relay it using 100k gas limit to
 * contracts that have enabled the fallback payable funciton.
 */
contract ETHRelayer {
    address payable public receiver;

    constructor(address payable _receiver) {
        receiver = _receiver;
    }

    receive() external payable {}

    function relay() public {
        (bool success, ) = receiver.call{gas: 100000, value: address(this).balance}("");
        require(success, "ETHRelayer: Relay transfer failed");
    }
}

contracts/utils/Multicall.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity >=0.5.0;
pragma experimental ABIEncoderV2;

// @title Multicall - Aggregate results from multiple read-only function calls
// @author Michael Elliot <mike@makerdao.com>
// @author Joshua Levine <joshua@makerdao.com>
// @author Nick Johnson <arachnid@notdot.net>

// Source: https://github.com/makerdao/multicall/blob/master/src/Multicall.sol

contract Multicall {
    struct Call {
        address target;
        bytes callData;
    }

    function aggregate(Call[] memory calls) public returns (uint256 blockNumber, bytes[] memory returnData) {
        blockNumber = block.number;
        returnData = new bytes[](calls.length);
        for (uint256 i = 0; i < calls.length; i++) {
            (bool success, bytes memory ret) = calls[i].target.call(calls[i].callData);
            require(success);
            returnData[i] = ret;
        }
    }

    // Helper functions
    function getEthBalance(address addr) public view returns (uint256 balance) {
        balance = addr.balance;
    }

    function getBlockHash(uint256 blockNumber) public view returns (bytes32 blockHash) {
        blockHash = blockhash(blockNumber);
    }

    function getLastBlockHash() public view returns (bytes32 blockHash) {
        blockHash = blockhash(block.number - 1);
    }

    function getCurrentBlockTimestamp() public view returns (uint256 timestamp) {
        timestamp = block.timestamp;
    }

    function getCurrentBlockDifficulty() public view returns (uint256 difficulty) {
        difficulty = block.difficulty;
    }

    function getCurrentBlockGasLimit() public view returns (uint256 gaslimit) {
        gaslimit = block.gaslimit;
    }

    function getCurrentBlockCoinbase() public view returns (address coinbase) {
        coinbase = block.coinbase;
    }
}
